svg.script:
var slideDelay = 3.2, select = function(s) {
      return document.querySelector(s);
    }, selectAll = function(s) {
      return document.querySelectorAll(s);
    }, xLink = "http://www.w3.org/1999/xlink", xmlns = "http://www.w3.org/2000/svg", t0 = select('.t0'), t1 = select('.t1'), t2 = select('.t2'), t3 = select('.t3'), t4 = select('.t4'), t5 = select('.t5'), t6 = select('.t6'),t6a = select('.t6a'), t7 = select('.t7'), t8 = select('.t8'), greyBox = select('.greyBox'), yellowBox = select('.yellowBox'), logoGroup = select('.logoGroup'), yellowLine = select('.yellowLine'), clickTag = select('.clickTag'), clickTitle = select('.clickTitle');

//get the URL from the object's param value if it's there
if(window.parent.document.querySelector("#clickTag")){

  var clickUrl = window.parent.document.querySelector("#clickTag").value;
  var clickInfo = window.parent.document.querySelector("#clickInfo").value;

  //assign the URL and title to the transparent overlay rect
  clickTag.setAttributeNS(xLink, 'href', clickUrl);
  clickTitle.textContent = clickInfo;
}

var tl = new TimelineMax({repeat:-1, delay:3});
tl.set([yellowBox, greyBox],{
  x:320,
  y:0,
  rotation:45,
  transformOrigin:'50% 50%',
  alpha:1
})
.set(logoGroup, {
  alpha:0,
  scale:0.9,
  transformOrigin:'50% 50%'
})
.set(yellowLine, {
  x:300
})
.set([t0],{
  attr:{
    x:60,
    y:140
  },
  alpha:0
})
.set([ t1],{
  attr:{
    x:20,
    y:120
  },
  alpha:0
})
.set([t2, t3],{
  attr:{
    x:20,
    y:140
  },
  alpha:0
})

.set([t4],{
  attr:{
    x:170,
    y:140
  },
  alpha:0
})
.set([t5],{
  attr:{
    x:20,
    y:190
  },
  alpha:0
})
.set([t6],{
  attr:{
    x:20,
    y:110
  },
  alpha:0
})
.set([t6a],{
  attr:{
    x:20,
    y:160
  },
  alpha:0
})
.set([t7],{
  attr:{
    x:20,
    y:160
  },
  alpha:0
})
.set([t8],{
  attr:{
    x:25,
    y:200
  },
  alpha:0
})

//END SETUP

.to(t0, 3, {
  attr:{
    x:20
  },
  alpha:1,
  ease:Power4.easeOut
})
.to(yellowBox, 2, {
  x:-250,
  ease:Power4.easeInOut
})
.to(t0, 1, {
  attr:{
    x:'-=10'
  },
  ease:Power2.easeIn,
  alpha:0
},'-=2')
.to(t1, 1, {
  alpha:1,
  ease:Power4.easeOut
}, '-=1')

.to(greyBox, 2, {
  x:-250,
  delay:slideDelay,
  ease:Power4.easeInOut
})

.to(t1, 1, {
  attr:{
    x:'-=10'
  },
  ease:Power2.easeIn,
  alpha:0
},'-=2')
.set([yellowBox, greyBox], {
  x:320
})
.to(t2, 1, {
  alpha:1,
  ease:Power4.easeOut
}, '-=1')
.to(t2, 1,{
  attr:{
    y:'-=50'
  },
  ease:Power1.easeInOut
})
.to(t3, 0.5,{
  alpha:1,
  ease:Power2.easeInOut
},'-=0.5')
.to([t4], 1,{
  alpha:1,
  ease:Power2.easeIn
})
.to([t5], 1,{
  alpha:1,
  ease:Power2.easeIn
})

.to(yellowBox, 2,{
  x:-250,
  delay:slideDelay,
  ease:Power3.easeInOut
})
.staggerTo([t2,t3, t4, t5], 1,{
  attr:{
    x:'-=20'
  },
  ease:Power2.easeIn,
  alpha:0
},0.051, '-=2')
.staggerTo([t6, t6a], 1, {
  alpha:1,
  ease:Power2.easeOut
},1, '-=1')

//empployment management goes UP
.staggerTo([t6, t6a], 1, {
  attr:{
    y:'-=60'
  },
  delay:slideDelay/2,
  ease:Power2.easeInOut
},0.05)
.to(t7, 1, {
  alpha:1,
  ease:Power2.easeIn
},'-=1')
.to(greyBox, 2,{
  x:-250,
  delay:slideDelay,
  ease:Power3.easeInOut
})
.staggerTo([t6, t6a,t7], 1,{
  attr:{
    x:'-=20'
  },
  alpha:0,
  ease:Power2.easeIn
},0.2, '-=2')
.to(logoGroup, 1, {
  scale:1,
  alpha:1
},'-=1')

//yellow line comes in

.to(logoGroup, 2, {
  scale:0.8,
  x:-80,
  y:-10,
  delay:slideDelay,
  ease:Back.easeInOut.config(0.4)
})

.to(yellowLine, 1, {
  x:25,
  ease:Power2.easeInOut
},'-=1')
.to(t8, 0.6, {
  attr:{
    y:210
  },
  alpha:1
},'-=0.2')

//Yellow line goes out
.to(yellowLine, 1, {
  x:-300,
  delay:slideDelay,
  ease:Power2.easeInOut
})
.to(logoGroup, 1, {
  alpha:0
},'-=1')
.to(t8, 1, {
  autoAlpha:0
},'-=1')

tl.timeScale(1.5);

TweenMax.set('svg', {
  visibility:'visible'
})

//tl.seek(10)
